USE [FacturaLabSQL]GO/****** Object:  StoredProcedure [dbo].[ObtenerTicket]    Script Date: 05/25/2014 20:27:37 ******/SET ANSI_NULLS ONGOSET QUOTED_IDENTIFIER ONGO-- =============================================-- Author:		<Author,,Name>-- Create date: <Create Date,,>-- Description:	<Description,,>-- =============================================ALTER PROCEDURE [dbo].[ObtenerTicket]	@idTicket bigintASBEGIN	SET NOCOUNT ON;	DECLARE @idAtencion bigint;	DECLARE @codigoOmega varchar(10);	select distinct @idAtencion = d.idAtencion from ticketdetalle d where d.idTicket = @idTicket;	select @codigoOmega = a.codOmega from atencion a where a.idAtencion = @idAtencion;		select t.idTicket, numTicket, t.numSerie, t.nomusuario, t.nomsede, 	(CONVERT(CHAR(11),t.fecha,101) + CONVERT(CHAR( 5),t.fecha,114)) 'fecha', t.total, t.nombre, t.direccion, t.impresora, 	(p.apepat +' '+ p.apemat + ' '+p.nombre) 'nombrePaciente', p.dni 'dniPaciente', t.tipopago, t.subtotal, t.igv, 	@codigoOmega 'codigoOmega', t.ruc	from ticket t, paciente p where p.idPaciente = t.idPaciente and t.idTicket = @idTicket;END